mapper.select:
SELECT MAX(id)
FROM EMPLOYEE
WHERE SUBSTR(id, 3, 2) = #{yearSuffix}

mapper.insert:
INSERT INTO employee_department (
id,
employee_id,
department_code
) VALUES (
employee_department_seq.NEXTVAL,
#{id, jdbcType=VARCHAR},
#{departmentCode, jdbcType=VARCHAR}
)

INSERT INTO employee_job (
id,
employee_id,
job_code
) VALUES (
employee_job_seq.NEXTVAL,
#{id, jdbcType=VARCHAR},
#{jobCode, jdbcType=VARCHAR}
)

INSERT INTO employee_organization (
id,
employee_id,
organization_code
) VALUES (
employee_organization_seq.NEXTVAL,
#{id, jdbcType=VARCHAR},
#{organizationCode, jdbcType=VARCHAR}
)

mapper.update:
UPDATE employee SET
phone = #{phone},
extension = #{extension},
email = #{email, jdbcType=VARCHAR}

    ,profile_url = #{profileUrl, jdbcType=VARCHAR}

WHERE id = #{id}

UPDATE employee
SET password = #{password}
WHERE ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM EMPLOYEE
WHERE EMAIL = #{email} AND id != #{userId}

SELECT COUNT(*)
FROM EMPLOYEE
WHERE PHONE = #{phone} AND id != #{userId}

mapper.update:
